package trimestre1;

public class ej019 {
/*Conteo Ascendente:Escribe un programa en Java que utilice un bucle `for` 
 * para contar del 1 al 10, imprimiendo cada número en una nueva línea.*/
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num;
		for(num=1; num<11; num++){
		System.out.println(num);
		}
	}

}
